mapper.select:
select MENUTITLE_NUM menuTitleNum, MENUTITLE_NAME menuTitleName, COM_ID comId
from menutitle
where COM_ID = #{comId}

select MENUTITLE_NUM menuTitleNum, MENUTITLE_NAME menuTitleName, COM_ID comId
from menutitle
where MENUTITLE_NUM=#{menuTitleNum}

mapper.update:
update menutitle
set MENUTITLE_NAME=#{menuTitleName}
where MENUTITLE_NUM=#{menuTitleNum}
